import  React, { Component } from 'react'
import Map from './mapStuff'

class Something extends Component {
    constructor(props) {
        super(props)
        this.state = {
            color : '',
            colorList : []
        }
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleClear = this.handleClear.bind(this)
    }

    handleChange(event) {
        this.setState({color: event.target.value})
        document.getElementById('color').style.backgroundColor = event.target.value
    }
    
    handleSubmit(event) {
        console.log(event.target.value)
        var colorlistThing = this.state.colorList
        colorlistThing.push(this.state.color)
        this.setState({colorList: colorlistThing})
        document.getElementById('color').style.backgroundColor = this.state.color
        event.preventDefault()
    }

    handleClear() {
        this.setState({colorList: []})

    }

    render() {
        console.log(this.state.colorList)
        return (
            <div id='input-field'>
                <form onSubmit={this.handleSubmit}>
                    <input value={this.state.color} onChange={this.handleChange} type='text' id='text'></input>
                    <input type="submit" value="Submit" id='mButton' />
                    <button onClick={this.handleClear} id='mButtontwo'>Clear</button>
                </form>
                <Map colors={this.state.colorList}/>
            </div>
        )
    }
    
}

export default Something
