import  React, { Component } from 'react'
import Map from './mapStuff'

const background =  document.getElementById('color').style
console.log(background.backgroundColor)

class Something extends Component {
    constructor(props) {
        super(props)
        this.state = {
            color : '',
            colorList : [],
            test : '',
        }
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleClear = this.handleClear.bind(this)
    }

    handleChange(event) {
        background.backgroundColor = this.state.color
        if(background.backgroundColor === ''&& this.state.color !='white') {
            this.setState({color: event.target.value, test: event.target.value})
        } else if (background.backgroundColor != '') {
            this.setState({color: event.target.value, test: ''})
            background.backgroundColor = ''
        }
        console.log(this.state.test)
        console.log(this.state.color)

    }
    
    handleSubmit(event) {
        background.backgroundColor = this.state.color
        console.log(background.backgroundColor)
        console.log(this.state.color.toLowerCase())
        if(background.backgroundColor === '' && this.state.color !== 'white') {

        } else if(this.state.colorList.includes(this.state.color.toLowerCase())) {
            background.backgroundColor = ''

        } else {
            background.backgroundColor = ''
            console.log(this.state.color)
            var colorlistThing = this.state.colorList
            colorlistThing.push(this.state.color.toLowerCase())
            this.setState({colorList: colorlistThing})
        }
        event.preventDefault()
    }

    handleClear() {
        this.setState({colorList: [], color: ''})
    }

    componentDidMount() {
        this.handleChange
    }

    render() {
        return (
            <div id='input-field'>
                <form onSubmit={this.handleSubmit}>
                    <h1 id='color2' style={{backgroundColor: this.state.color, color: 'Black'}} >{this.state.test}</h1>
                    <input value={this.state.color} onChange={this.handleChange} type='text' id='text'></input>
                    <input type="submit" value="Save" id='mButton' />
                    <button onClick={this.handleClear} id='mButtontwo'>Clear</button>
                </form>
                <div id='buttonDisplay'>
                    <Map colors={this.state.colorList}/>
                </div>
            </div>
        )
    }
    
}

export default Something
