import  React, { Component } from 'react'
import Map from './mapStuff'

const background =  document.getElementById('color').style
console.log(background.backgroundColor)

class Something extends Component {
    constructor(props) {
        super(props)
        this.state = {
            color : '',
            colorList : [],
        }
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleClear = this.handleClear.bind(this)
    }

    handleChange(event) {
        this.setState({color: event.target.value})
        background.backgroundColor = event.target.value
        console.log("background" + background.backgroundColor)
    }
    
    handleSubmit(event) {
        background.backgroundColor = this.state.color
        console.log(background.backgroundColor)
        console.log(this.state.color)
        if(background.backgroundColor === '' && this.state.color !== 'white') {

        } else {
            console.log(this.state.color)
            var colorlistThing = this.state.colorList
            colorlistThing.push(this.state.color)
            this.setState({colorList: colorlistThing})
        }
        event.preventDefault()
    }

    handleClear() {
        this.setState({colorList: []})
    }

    render() {
        return (
            <div id='input-field'>
                <form onSubmit={this.handleSubmit}>
                    <h1 style={{backgroundColor: this.state.color}, {color: 'white'}} >Test</h1>
                    <input value={this.state.color} onChange={this.handleChange} type='text' id='text'></input>
                    <input type="submit" value="Submit" id='mButton' />
                    <button onClick={this.handleClear} id='mButtontwo'>Clear</button>
                </form>
                <Map colors={this.state.colorList}/>
            </div>
        )
    }
    
}

export default Something
